        /// string to the given <see cref="StringBuilder"/>.
        /// </summary>
        internal static StringBuilder AppendToPat(StringBuilder buf, string s, bool escapeUnprintable)
        {
            int cp;
            for (int i = 0; i < s.Length; i += Character.CharCount(cp))
