        /// <summary>
        /// Append the <see cref="ToPattern(bool)"/> representation of a
        /// character to the given <see cref="StringBuilder"/>.
        /// </summary>
        internal static StringBuilder AppendToPat(StringBuilder buf, int c, bool escapeUnprintable)
        {
            try
            {
                if (escapeUnprintable && Utility.IsUnprintable(c))
                {
                    // Use hex escape notation (<backslash>uxxxx or <backslash>Uxxxxxxxx) for anything
                    // unprintable
                    if (Utility.EscapeUnprintable(buf, c))
                    {
                        return buf;
                    }
                }
                // Okay to let ':' pass through
                switch (c)
                {
                    case '[': // SET_OPEN:
                    case ']': // SET_CLOSE:
                    case '-': // HYPHEN:
                    case '^': // COMPLEMENT:
                    case '&': // INTERSECTION:
                    case '\\': //BACKSLASH:
                    case '{':
                    case '}':
                    case '$':
                    case ':':
                        buf.Append('\\');
                        break;
                    default:
                        // Escape whitespace
                        if (PatternProps.IsWhiteSpace(c))
                        {
                            buf.Append('\\');
                        }
                        break;
                }
                AppendCodePoint(buf, c);
                return buf;
            }
            catch (IOException e)
            {
                throw new ICUUncheckedIOException(e);
            }
        }

