        private StringBuilder AppendNewPattern(StringBuilder result, bool escapeUnprintable, bool includeStrings)
        {
            try
            {
                result.Append('[');

                int count = RangeCount;

                // If the set contains at least 2 intervals and includes both
                // MIN_VALUE and MAX_VALUE, then the inverse representation will
                // be more economical.
                if (count > 1 &&
                        GetRangeStart(0) == MinValue &&
                        GetRangeEnd(count - 1) == MaxValue)
                {

                    // Emit the inverse
                    result.Append('^');

                    for (int i = 1; i < count; ++i)
                    {
                        int start = GetRangeEnd(i - 1) + 1;
                        int end = GetRangeStart(i) - 1;
                        AppendToPat(result, start, escapeUnprintable);
                        if (start != end)
                        {
                            if ((start + 1) != end)
                            {
                                result.Append('-');
                            }
                            AppendToPat(result, end, escapeUnprintable);
                        }
                    }
                }

                // Default; emit the ranges as pairs
                else
                {
                    for (int i = 0; i < count; ++i)
                    {
                        int start = GetRangeStart(i);
                        int end = GetRangeEnd(i);
                        AppendToPat(result, start, escapeUnprintable);
                        if (start != end)
                        {
                            if ((start + 1) != end)
                            {
                                result.Append('-');
                            }
                            AppendToPat(result, end, escapeUnprintable);
                        }
                    }
                }

                if (includeStrings && strings.Count > 0)
                {
                    foreach (string s in strings)
                    {
                        result.Append('{');
                        AppendToPat(result, s, escapeUnprintable);
                        result.Append('}');
                    }
                }
                result.Append(']');
                return result;
            }
            catch (IOException e)
            {
                throw new ICUUncheckedIOException(e);
            }
        }

